import React, { useState, useRef, useEffect } from "react";
import Slide from "../Components/Slide";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  margin: auto;
  height: 1000px;
  overflow: hidden;
`;

const Button = styled.div`
  all: unset;
  padding: 1em 2em;
  margin: 2em 2em;
  color: burlywood;
  border-radius: 10px;
  border: 1px solid burlywood;
  &:hover {
    background-color: burlywood;
    color: #fff;
  }
`;

const SliderContainer = styled.div`
  margin: 0 auto;
  margin-bottom: 2em;
  display: flex; 
`;


const Text = styled.div`
  text-align: center;
  color: burlywood;
  p {
    color: #fff;
    font-size: 20px;
    background-color: burlywood;
    display: inline-block;
    border-radius: 50px;
    padding: 0.5em 1em;
  }
`;

const Center = styled.div`
  text-align: center;
`;

const TotalSlids = 3;

const Slider = () => {
  return (
    <>
    <Container>
    <Text>
        <h1>호두 아가 시절</h1>
        <p>n번 째 사진</p>
      </Text>
      <SliderContainer>
        <Slide img={img1} />
        <Slide img={img2} />
        <Slide img={img3} />
      </SliderContainer>
      <Center>
        <Button>Prev</Button>
        <Button>Next</Button>
      </Center>
    </Container>
    </>
  );
};

export default Slider;
